import { useState } from "react";
import NewExpense from "./components/Expense/ExpenseForm/NewExpense";
import Expenses from "./components/Expense/Expenses";

function App() {
  let expenses = [
    {
      id: 'e1',
      title: 'Toilet Paper',
      amount: 94.12,
      date: new Date(2020, 7, 14),
    },
    { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: 'e3',
      title: 'Car Insurance',
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: 'e4',
      title: 'New Desk (Wooden)',
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ];

  const [expensesList, updateExpensesList] = useState(expenses);

  const addExpenseHandler = (expense) => {
    expenses.push(expense);

    console.log(expense);
    console.log(expenses);

    expenseListUpdater();
  }

  const expenseListUpdater = (event) => {
    updateExpensesList(event.target.value);
  }

  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
      <Expenses items={expensesList} />
    </div>
  );
}

export default App;
